import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext'; // Import the useAuth hook for authentication
import './Courses.css'; // Importing the CSS file for styling

const Courses = () => {
  const navigate = useNavigate();
  const { isAuthenticated } = useAuth(); // Get the authentication status from AuthContext

  const courses = [
    {
      id: 1,
      title: 'java full stack',
      description: 'This latest java full stack course covers everything from basics to advance.',
      img: 'https://cdn.prod.website-files.com/6344c9cef89d6f2270a38908/63862296680a877e33cb39f9_c4.webp',
    },
    {
      id: 2,
      title: 'React js full courses',
      description: 'This latest java full stack course covers everything from basics to advance.',
      img: 'data:image/jpeg;base64,',
    },
    {
      id: 3,
      title: 'UP Coming',
      description: 'More courses coming soon.',
      img: 'data:image/jpeg;base64,',
    },
  ];

  const handleViewCourse = (id) => {
    if (isAuthenticated) {
      navigate(`/course/${id}`); // Redirect to course details if logged in
    } else {
      navigate('/login'); // Redirect to login page if not logged in
    }
  };

  return (
    <div className="courses-page">
      <h1 className="page-title">Courses</h1>
      <div className="courses-grid">
        {courses.map((course) => (
          <div key={course.id} className="course-card">
            <img src={course.img} alt={course.title} className="course-image" />
            <div className="course-content">
              <h3>{course.title}</h3>
              <p>{course.description}</p>
              <button
                onClick={() => handleViewCourse(course.id)}
                className="course-link"
              >
                View Course
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Courses;
